INSERT INTO `book_detial_info` VALUES (7, '建党百年百篇文学短经典（全5册）', ' 小说文学', '贺绍俊等', 'https://images-cn.ssl-images-amazon.cn/images/I/51eHZlhHKGS.jpg', '为庆祝中国共产党成立一百周年，以符合广大读者欣赏习惯的内容和形式，弘扬红色传统，传承红色基因，凝聚爱党爱国之情，砥砺强国兴邦之志，人民<a href=\"https://sobooks.cc/books/tag/%e6%96%87%e5%ad%a6\" title=\"\" target=\"_blank\" data-original-title=\"查看更多关于文学的文章\">文学</a>出版社策划推出“建党百年百篇<a href=\"https://sobooks.cc/books/tag/%e6%96%87%e5%ad%a6\" title=\"\" target=\"_blank\" data-original-title=\"查看更多关于文学的文章\">文学</a>短<a href=\"https://sobooks.cc/books/tag/%e7%bb%8f%e5%85%b8\" title=\"\" target=\"_blank\" data-original-title=\"查看更多关于经典的文章\">经典</a>”，邀请密切关注和深刻了解现当代<a href=\"https://sobooks.cc/books/tag/%e6%96%87%e5%ad%a6\" title=\"\" target=\"_blank\" data-original-title=\"查看更多关于文学的文章\">文学</a>发展的专家，经反复讨论，从反映建党百年光辉历程的优秀<a href=\"https://sobooks.cc/books/tag/%e6%96%87%e5%ad%a6\" title=\"\" target=\"_blank\" data-original-title=\"查看更多关于文学的文章\">文学</a>作品中，精选出思想精深、艺术精湛、篇幅精悍的中短篇小说与散文纪实类作品共一百篇。', '2021-08-18', '文学');
