INSERT INTO `book_detial_info` VALUES (190, '别睡，这里有蛇', ' 人文社科', '丹尼尔・埃弗里特', 'https://img9.doubanio.com/lpic/s32295310.jpg', '1977年，丹尼尔·埃弗里特携妻子和三个年幼的孩子来到亚马孙丛林中皮拉罕人的部落，他想要传教，改变皮拉罕人的宗教信仰。但他发现皮拉罕语违背了所有现存的语言理论，并反映出一种远离当代认识的生活方式。例如皮拉罕人没有记数系统，没有统一的颜色的称谓，没有战争和个人财产的概念，没有过去与未来，完全活在当下。埃弗里特开始痴迷于他们的语言、文化，并沉溺于他们的生活方式，久而久之，他最终失去了传教的信念。', '2021-06-25', '人类');
INSERT INTO `book_detial_info` VALUES (191, '下流社会', ' 人文社科', '三浦展', 'https://img9.doubanio.com/lpic/s29878520.jpg', '曾经令<a href=\"https://sobooks.cc/books/tag/riben\" title=\"\" target=\"_blank\" data-original-title=\"查看更多关于日本的文章\">日本</a>骄傲的一亿中流阶层，越来越多跌入下流', '2021-06-25', '日本');
